#!/usr/bin/env python3
"""
Create a beautiful desktop icon for DMAI
"""

import os
import base64
from pathlib import Path

# Base64 encoded icon (small but nice)
ICON_DATA = '''

'''

def create_icon():
    desktop = Path.home() / "Desktop"
    icon_path = desktop / "DMAI.icns"
    
    # Decode and save icon
    with open(icon_path, 'wb') as f:
        f.write(base64.b64decode(ICON_DATA))
    
    print(f"âœ… Icon created: {icon_path}")

if __name__ == "__main__":
    create_icon()
